function ListView(){
  const[q,setQ]=useState('');
  const[openId,setOpenId]=useState(null);
  const consMap=useMemo(()=>{const m={};CONS.forEach(c=>m[c.id]=c);return m},[]);

  const filtered=useMemo(()=>{
    if(!q)return HUBS;
    const f=q.toLowerCase();
    return HUBS.filter(h=>
      h.w.includes(q)||(h.m||'').toLowerCase().includes(f)||
      h.ch.some(c=>c.w.includes(q)||(c.e||'').toLowerCase().includes(f))
    );
  },[q]);

  return(<div>
    <input type="text" placeholder="Search hub, word, or meaning..." value={q} onChange={e=>setQ(e.target.value)}
      style={{width:'100%',padding:'9px 12px',borderRadius:10,border:'1px solid rgba(255,255,255,.08)',background:'rgba(255,255,255,.03)',color:'#F5E6C8',fontSize:14,outline:'none',marginBottom:10,boxSizing:'border-box'}}/>
    <div style={{maxHeight:440,overflowY:'auto'}}>
      {filtered.slice(0,80).map(h=>{const open=openId===h.id;const cn=consMap[h.c];return(
        <div key={h.id}>
          <button onClick={()=>setOpenId(open?null:h.id)} style={{width:'100%',display:'flex',justifyContent:'space-between',alignItems:'center',padding:'7px 8px',background:open?'rgba(200,169,81,.05)':'transparent',border:'none',borderBottom:'1px solid rgba(255,255,255,.03)',cursor:'pointer'}}>
            <div style={{textAlign:'left',display:'flex',alignItems:'center',gap:6}}>
              <span style={{fontSize:12,color:'#555',background:'rgba(200,169,81,.08)',padding:'1px 5px',borderRadius:5}}>{cn?.b||'?'}</span>
              <span style={{fontSize:16,color:S.gold,fontWeight:600}}>{h.w}</span>
            </div>
            <div style={{display:'flex',alignItems:'center',gap:6}}>
              <span style={{fontSize:11,color:'#999',maxWidth:120,overflow:'hidden',textOverflow:'ellipsis',whiteSpace:'nowrap'}}>{h.m}</span>
              <span style={{fontSize:10,color:S.blue}}>{h.ch.length}w</span>
              <span style={{color:'#444',fontSize:10,transform:open?'rotate(90deg)':'none',transition:'transform .15s'}}>▶</span>
            </div>
          </button>
          {open&&h.ch.length>0&&(
            <div style={{padding:'4px 10px 8px 30px',background:'rgba(200,169,81,.02)'}}>
              {h.ch.map((c,i)=>{const d=toDev(c.w);return(
                <div key={i} style={{display:'flex',justifyContent:'space-between',padding:'4px 0',borderBottom:i<h.ch.length-1?'1px solid rgba(255,255,255,.02)':'none'}}>
                  <div><span style={{fontSize:14,color:'#F5E6C8'}}>{c.w}</span><span style={{fontSize:10,color:'#555',marginLeft:6}}>{d}</span></div>
                  <span style={{fontSize:11,color:S.blue}}>{c.e}</span>
                </div>
              )})}
            </div>
          )}
        </div>
      )})}
    </div>
    <div style={{fontSize:9,color:'#444',textAlign:'center',marginTop:6}}>{Math.min(filtered.length,80)} of {filtered.length} hubs</div>
  </div>)
}

// ============================================================
// VIEW 4: CARDS — Tap-to-reveal with consonant filter
// ============================================================
function CardsView(){
  const[flipped,setFlipped]=useState({});
  const[filt,setFilt]=useState('all');
  const consWithData=useMemo(()=>CONS.filter(c=>c.h>0),[]);
  const filtered=useMemo(()=>{
    const hs=filt==='all'?HUBS:HUBS.filter(h=>h.c===parseInt(filt));
    // flatten: hubs + their children as cards
    const cards=[];
    hs.forEach(h=>{
      cards.push({id:'h'+h.id,w:h.w,e:h.m,type:'hub',nch:h.ch.length});
      h.ch.forEach((c,i)=>cards.push({id:'c'+h.id+'_'+i,w:c.w,e:c.e,type:'spoke',hub:h.w}));
    });
    return cards;
  },[filt]);

  return(<div>
    <div style={{display:'flex',gap:4,marginBottom:10,flexWrap:'wrap'}}>
      <Pill a={filt==='all'} o={()=>setFilt('all')}>All</Pill>
      {consWithData.slice(0,14).map(c=><Pill key={c.id} a={filt===String(c.id)} o={()=>setFilt(String(c.id))}>{c.b} ({c.h})</Pill>)}
    </div>
    <div style={{display:'grid',gridTemplateColumns:'repeat(auto-fill,minmax(120px,1fr))',gap:7,maxHeight:430,overflowY:'auto'}}>
      {filtered.slice(0,80).map(c=>{const fl=flipped[c.id];const isHub=c.type==='hub';return(
        <button key={c.id} onClick={()=>setFlipped(p=>({...p,[c.id]:!p[c.id]}))} style={{
          background:fl?(isHub?'rgba(232,132,92,.12)':'rgba(107,164,184,.08)'):(isHub?'rgba(232,132,92,.05)':'rgba(255,255,255,.02)'),
          border:`1px solid ${isHub?'rgba(232,132,92,.2)':'rgba(107,164,184,.1)'}`,borderRadius:12,padding:10,cursor:'pointer',textAlign:'center',minHeight:72,
          display:'flex',flexDirection:'column',justifyContent:'center',alignItems:'center',
        }}>
          {!fl?(<>
            <div style={{fontSize:isHub?17:15,color:isHub?S.orange:'#F5E6C8',fontWeight:isHub?700:400}}>{c.w}</div>
            {isHub&&<div style={{fontSize:8,color:'#666',marginTop:2}}>{c.nch} words</div>}
            {!isHub&&<div style={{fontSize:8,color:'#444',marginTop:2}}>tap</div>}
          </>):(<>
            <div style={{fontSize:13,color:'#F5E6C8'}}>{c.w}</div>
            <div style={{fontSize:10,color:'#777'}}>{toDev(c.w)}</div>
            <div style={{fontSize:11,color:isHub?S.orange:S.blue,fontWeight:600,marginTop:2}}>{c.e}</div>
            {c.hub&&<div style={{fontSize:9,color:'#555',marginTop:2}}>← {c.hub}</div>}
          </>)}
        </button>
      )})}
    </div>
    <div style={{fontSize:9,color:'#444',textAlign:'center',marginTop:6}}>{Math.min(filtered.length,80)} of {filtered.length}</div>
  </div>)
}

function Pill({children,a,o}){return(<button onClick={o} style={{padding:'3px 10px',borderRadius:14,fontSize:10,cursor:'pointer',border:`1px solid ${a?S.gold:'rgba(255,255,255,.06)'}`,background:a?'rgba(200,169,81,.18)':'transparent',color:a?S.gold:'#555'}}>{children}</button>)}

